import { Button, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaPlay } from "react-icons/fa";
import React, { useState, useEffect } from "react";

function PlayButton() {
  const [audioList, setAudioList] = useState([]);
  const [selectedAudio, setSelectedAudio] = useState("");

  // Fetch the list of audio files from the backend
  async function fetchAudioList() {
    try {
      const response = await fetch("/audio");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setAudioList(data);
    } catch (error) {
      console.error("Failed to fetch audio list:", error);
    }
  }

  useEffect(() => {
    fetchAudioList();
  }, []);

  // Load the audio list when the component mounts
  useEffect(() => {
    async function getAudioList() {
      const list = await fetchAudioList();
      setAudioList(list);
    }

    getAudioList();
  }, []);

  // Function to handle play button click
  const handlePlay = (filePath) => {
    setSelectedAudio(filePath);
  };

  return (
    <div className="container">
      <h1>Audio Player</h1>
      <div className="row">
        <button>
          <FaPlay />
        </button>
      </div>
      {selectedAudio && (
        <audio controls autoPlay>
          <source src={`/audio/${selectedAudio}`} type="audio/mpeg" />
          Your browser does not support the audio element.
        </audio>
      )}
    </div>
  );
}

export default PlayButton;
