import { Button, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaPlay } from "react-icons/fa";
import React, { useState, useEffect } from "react";

function formatTime(seconds) {
  const minutes = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${minutes}:${secs < 10 ? "0" : ""}${secs}`;
}

function PlayButton({
  playAndPause,
  currentSong,
  songPlaying,
  audioRef,
  volume,
  setVolume,
}) {
  const [currentTime, setCurrentTime] = useState(0);
  const [songDuration, setSongDuration] = useState(0);

  const songTime = () => {
    if (audioRef.current) {
      setCurrentTime(audioRef.current.currentTime);
      setSongDuration(audioRef.current.duration);
    }
  };

  useEffect(() => {
    const interval = setInterval(songTime, 1000);
    return () => clearInterval(interval);
  }, []);

  const volumeBarChange = (event) => {
    if (audioRef.current) {
      setVolume(event.target.value);
      audioRef.current.volume = volume;
    }
  };

  return (
    <div className="container">
      <button onClick={playAndPause}>
        <FaPlay />
      </button>
      {(currentSong || songPlaying) && (
        <div>
          <audio ref={audioRef} key={currentSong || songPlaying}>
            <source src={currentSong || songPlaying} type="audio/mp4" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
      <input
        type="range"
        max={1}
        min={0}
        step={0.02}
        value={volume}
        onChange={volumeBarChange}
      />
      <p>
        {formatTime(currentTime)}: {formatTime(songDuration)}
      </p>
    </div>
  );
}

export default PlayButton;
