import { Col, Container, Row } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect, useRef } from "react";
import SearchBar from "./SearchBar";
import History from "./History";
import PlayButton from "./PlayButton";
// import "./App.css";

function App() {
  const [audioList, setAudioList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [songPlaying, setSongPlaying] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [songTitle, setSongTitle] = useState(null);
  const [volume, setVolume] = useState(0);
  const audioRef = useRef(null);

  const playButton = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play();
        setIsPlaying(false);
      } else {
        audioRef.current.pause();
        setIsPlaying(true);
      }
    }
  };

  return (
    <div class=" bg-black h-screen grid-rows-2">
      <div class="flex flex-row gap-x-10 overflow-auto">
        <div class="basis-1/4 bg-gray-500 hover:bg-gray-600 rounded-lg shadow-custom-shadow ">
          <History
            currentSong={currentSong}
            songPlaying={songPlaying}
            songTitle={songTitle}
            setCurrentSong={setCurrentSong}
            setSongPlaying={setSongPlaying}
            setSongTitle={setSongTitle}
          />
        </div>
        <div class="basis-3/4 bg-gray-500 rounded-lg shadow-custom-shadow">
          <SearchBar songTitle={songTitle} />
        </div>
      </div>
      <div class="fixed bottom-0 left-0 right-0 bg-gray-800 p-4">
        <PlayButton
          playAndPause={playButton}
          currentSong={currentSong}
          songPlaying={songPlaying}
          audioRef={audioRef}
          volume={volume}
          setVolume={setVolume}
        />
      </div>
    </div>
  );
}

export default App;
