import { Col, Container, Row } from "react-bootstrap";
import { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./SearchBar.css";
import VideoPlayer from "./components/VideoPlayer";

function SearchBar({
  songTitle,
  setCurrentVideo,
  currentVideo,
  setVideoPlaying,
  videoPlaying,
}) {
  const [amount, setAmount] = useState("");
  const [artist, setArtist] = useState("");
  const [name, setName] = useState("");

  console.log("Amount:", amount);
  console.log("Artist:", artist);
  console.log("Name:", name);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("/submit_form", {
        method: "POST",
        headers: {
          "content-type": "application/json",
        },
        body: JSON.stringify({ amount: amount, artist: artist, name: name }),
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const result = await response.json();

      console.log(result);
    } catch (error) {
      console.error("There was a problem with the fetch operation:", error);
    }
  };

  return (
    <Container>
      <form onSubmit={handleSubmit}>
        <Row className="center-items">
          <Col className="mt-3">
            <input
              className="enabled:focus:shadow-md shadow-blue-400/70 form-control custom-input bg-transparent shadow-md shadow-blue-400/70"
              id="song-amount"
              type="text"
              value={amount}
              placeholder="Amount of songs: "
              onChange={(e) => setAmount(e.target.value)}
            />
          </Col>
          <Col className="mt-3">
            <input
              className="form-control custom-input bg-transparent shadow-md shadow-blue-400/70 enabled:focus:shadow-md shadow-blue-400/70"
              id="song-artist"
              value={artist}
              placeholder="Song Artist: "
              type="text"
              onChange={(e) => setArtist(e.target.value)}
            />
          </Col>
          <Col className="mt-3">
            <input
              className="form-control custom-input bg-transparent shadow-md shadow-blue-400/70 enabled:focus:shadow-md shadow-blue-400/70 "
              id="song-name"
              value={name}
              placeholder="Song Name: "
              type="text"
              onChange={(e) => setName(e.target.value)}
            />
          </Col>
          <Col>
            <button
              type="submit"
              className="shadow-md shadow-blue-400/70 text-blue-400 rounded-md border-2 border-blue-400 p-2 mt-3.5 mb-2 hover:bg-white/10 transition-colors duration-300"
            >
              Search
            </button>
          </Col>
        </Row>
      </form>
      <Row className="text-blue-400 text-2xl custom-text text-shadow-glowing mb-4 mt-3">
        <h1>Song Playing: {songTitle}</h1>
      </Row>
      <VideoPlayer currentVideo={currentVideo} videoPlaying={videoPlaying} />
    </Container>
  );
}

export default SearchBar;
