import { Button, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { FaPlay } from "react-icons/fa";
import React, { useState, useEffect } from "react";

function PlayButton({ playAndPause, currentSong, songPlaying, audioRef }) {
  return (
    <div className="container">
      <button onClick={playAndPause}>
        <FaPlay />
      </button>
      {(currentSong || songPlaying) && (
        <div>
          <audio ref={audioRef} key={currentSong || songPlaying}>
            <source src={currentSong || songPlaying} type="audio/mp4" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
}

export default PlayButton;
