import { Col, Container, Row } from "react-bootstrap";
import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function SearchBar() {
  const [message, setMessage] = useState("");
  const [songName, setSongName] = useState("");

  const handleSearch = async () => {
    const userInput = document.getElementById("song-search").value;
    const response = fetch("/YoutubeAudioDownload", {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: `song-search=${userInput}`,
    });
  };

  return (
    <Container>
      <label htmlFor="song-search">Find your song:</label>
      <Row>
        <Col>
          <input
            className="form-control"
            id="song-search"
            type="text"
            onChange={(e) => setSongName(e.target.value)}
          />
        </Col>
        <Col>
          <button onClick={handleSearch} id="search-button">
            Search
          </button>
        </Col>
      </Row>
    </Container>
  );
}

export default SearchBar;
