import { Col, Container, Row } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
import History from "./History";
import PlayButton from "./PlayButton";
import "./App.css";

function App() {
  const [audioList, setAudioList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [songPlaying, setSongPlaying] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [songTitle, setSongTitle] = useState(null);
  const audioRef = useRef(null);

  const playButton = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play();
        setIsPlaying(false);
      } else {
        audioRef.current.pause();
        setIsPlaying(true);
      }
    }
  };

  function switchSongs(item) {
    if (currentSong === null && songPlaying === null) {
      setCurrentSong(`/${item.file_name}`);
      setSongPlaying(null);
      setSongTitle(item.song_name);
    } else if (currentSong !== null && songPlaying === null) {
      setCurrentSong(null);
      setSongPlaying(`/${item.file_name}`);
      setSongTitle(item.song_name);
    } else if (currentSong === null && songPlaying !== null) {
      setCurrentSong(`/${item.file_name}`);
      setSongPlaying(null);
      setSongTitle(item.song_name);
    } else {
      console.log("bad things");
    }
  }

  return (
    <Container fluid className="d-flex flex-column" style={{ height: "100vh" }}>
      <Row className="flex-grow-1">
        <Col className="border border-dark d-flex flex-column" xs={2} md={2}>
          <History />
        </Col>
        <Col className="border border-dark d-flex flex-column" xs={10} md={10}>
          <SearchBar />
        </Col>
      </Row>
      <Row className="border border-dark">
        <PlayButton />
      </Row>
    </Container>
  );
}

export default App;
