import React, { useState, useEffect, useRef } from "react";
import { Container, Row, Col } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import PlayButton from "../PlayButton";

function SongList() {
  const [audioList, setAudioList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentSong, setCurrentSong] = useState(null);
  const [songPlaying, setSongPlaying] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  async function fetchAudioList() {
    setLoading(true);
    try {
      const response = await fetch("/song_request");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const result = await response.json();
      setAudioList(result);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  }

  useEffect(() => {
    fetchAudioList();
  }, []);

  const playButton = () => {
    if (audioRef.current()) {
      if (isPlaying) {
        audioRef.current.play();
        setIsPlaying(false);
      } else if (!isPlaying) {
        audioRef.current.pause();
        setIsPlaying(true);
      }
    }
  };

  function switchSongs(item) {
    if (currentSong === null && songPlaying === null) {
      setCurrentSong(`/${item.file_name}`);
      setSongPlaying(null);
    } else if (currentSong !== null && songPlaying === null) {
      setCurrentSong(null);
      setSongPlaying(`/${item.file_name}`);
    } else if (currentSong === null && songPlaying !== null) {
      setCurrentSong(`/${item.file_name}`);
      setSongPlaying(null);
    } else {
      console.log("bad things");
    }
  }

  console.log("current song", currentSong);
  console.log("song playing", songPlaying);

  const handleDelete = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("/delete_songs", {
        method: "POST",
        headers: {
          "content-type": "application/json",
        },
      });

      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const result = await response.json();

      console.log(result);
    } catch (error) {
      console.error("There was a problem with the fetch operation:", error);
    }
  };

  return (
    <Container>
      <h1>Song List</h1>
      <button onClick={handleDelete} variant="danger">
        Reset Songs
      </button>
      {loading && <p>Loading...</p>}
      {error && <p>Error: {error}</p>}
      <Row>
        <Col>
          <ul>
            {audioList.map((item, index) => (
              <li
                key={index}
                onClick={() => switchSongs(item)}
                style={{ cursor: "pointer" }}
              >
                {item.song_name}
              </li>
            ))}
          </ul>
          {(currentSong || songPlaying) && (
            <audio ref={audioRef} key={currentSong || songPlaying}>
              <source src={currentSong || songPlaying} type="audio/mp4" />
              Your browser does not support the audio element.
              <PlayButton playAndPause={playButton} />
            </audio>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default SongList;
